            $("#summernote").summernote("code", localStorage.getItem("summernotedata"));
            }},
            height: 300,
            toolbar: [

                ['img', ['picture']],
                ['style', ['style', 'addclass', 'clear']],
                ['fontstyle', ['bold', 'italic', 'ul', 'ol', 'link', 'paragraph']],
