sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;


	$rootScope.$on('selectcourse', function(event,data){
      c.server.get({
                  action: 'getcourse',
                  courseID: $rootScope.courseID
              }).then(function(r) {
                  console.log(r.data);
                  c.data= r.data;
				          c.data.unitlist=r.data.unitlist;



				          console.log(c.data.unitlist);









              });
      });


}

